import re

def find_bonus_sentence(line):
  bonus_sentences = [i for i in re.findall("\((.*)\)", line) if sentence_valid(i)]
  return bonus_sentences

def sentence_valid(str):
  forbidden = ("Sg.", "Pl.", "+", "ohne Artikel")
  for e in forbidden:
    if str.find(e) != -1:
      return False
  else:
    if str == "sich":
      return False
    else:
      return True

with open("wordlist.txt", 'r') as f:
  for line in f:
    sentence = find_bonus_sentence(line)
    print(sentence)